export function ProfileIcon({ settings, size, className, initials }: { settings: Iplayer['settings'], size?: number, className?: string, initials?: string }) {
	const resize = size + "rem";
	
	if (!settings) {
		return null;
	}

	if (settings.hasPicture) {
		return (
			<div style={{ backgroundColor: settings.backgroundColor, width: resize, height: resize, border: `${settings.borderWidth}px solid ${settings.borderColor}` }} className={`relative rounded-full flex items-center justify-center cursor-pointer duration-200 select-none ${className}`}>
				<div style={{ width: resize, height: resize }} className="flex items-center justify-center overflow-hidden">
					<ProfilePicture name={settings.selectedPicture} color={settings.textColor} className={`${size! >= 3 ? 'p-2' : 'p-1'}`} />
				</div>
			</div>
		)
	}

	return (
		<div style={{ backgroundColor: settings.backgroundColor, color: settings.textColor, width: resize, height: resize, border: `${settings.borderWidth}px solid ${settings.borderColor}` }} className={`relative rounded-full flex items-center justify-center cursor-pointer duration-200 select-none ${className}`}>
			{
				initials ?
					<span>{initials}</span> :
					<span>{getUserInitials()}</span>
			}
		</div>
	)
}